class ExtraItem {

    readonly id: ExtraType;
    readonly category: ExtraCategory;
    readonly name: string;
    readonly svg: string;

    constructor(id: ExtraType, category: ExtraCategory, name: string, svg: string) {
        this.id = id;
        this.category = category;
        this.name = name;
        this.svg = svg;
    }
}


class ExtrasCatalog {

    private static _instance: ExtrasCatalog;

    private items = [
        new ExtraItem(ExtraType.None, ExtraCategory.Ear, "None", ""),
        new ExtraItem(ExtraType.BajoranEarring, ExtraCategory.Ear, "Bajoran Earring", BajoranEarring),
        new ExtraItem(ExtraType.SimpleEarring, ExtraCategory.Ear, "Simple Stud Earring", SimpleEarring),
        new ExtraItem(ExtraType.HoopEarring, ExtraCategory.Ear, "Hoop Earring", HoopEarring),

        new ExtraItem(ExtraType.None, ExtraCategory.Forehead, "None", ""),
        new ExtraItem(ExtraType.SmallBindi, ExtraCategory.Forehead, "Small Bindi", SmallBindi),
        new ExtraItem(ExtraType.RisanSymbol, ExtraCategory.Forehead, "Risan Symbol", RisanSymbol),
        new ExtraItem(ExtraType.InuitTattoo, ExtraCategory.Forehead, "Inuit Tattoo", InuitTattoo),

        new ExtraItem(ExtraType.None, ExtraCategory.Headwear, "None", ""),
        new ExtraItem(ExtraType.FerengiHeadFlap, ExtraCategory.Headwear, "Ferengi Head Flap", FerengiHeadFlap),
        new ExtraItem(ExtraType.SecurityHelmet, ExtraCategory.Headwear, "Security Helmet", SecurityHelmet),
        new ExtraItem(ExtraType.BynarHeadpiece1, ExtraCategory.Headwear, "Bynar Headpiece 1", BynarHeadpiece1),
        new ExtraItem(ExtraType.BynarHeadpiece2, ExtraCategory.Headwear, "Bynar Headpiece 2", BynarHeadpiece2),

        new ExtraItem(ExtraType.None, ExtraCategory.Face, "None", ""),
        new ExtraItem(ExtraType.ZaraniteMask, ExtraCategory.Face, "Zaranite Breather Mask", ZaraniteBreatherMask),
        new ExtraItem(ExtraType.Visor, ExtraCategory.Face, "Visor", Visor),
    ]

    public static get instance() {
        if (ExtrasCatalog._instance == null) {
            ExtrasCatalog._instance = new ExtrasCatalog();
        }
        return ExtrasCatalog._instance;
    }

    getExtras(token: Token, category: ExtraCategory, back: boolean = false) {
        return this.items
            .filter(i => i.category === category && token.extras.indexOf(i.id) >= 0)
            .filter(i => i.id !== ExtraType.FerengiHeadFlap || back)
            .map(i => {if (i.id === ExtraType.FerengiHeadFlap && DivisionColors.isDivisionColorsSupported(token.uniformEra)) {
                if (token.uniformEra === UniformEra.DominionWar) {
                    return i.svg.replace(DefaultRed, "#7d7d7d");
                } else {
                    return i.svg.replace(DefaultRed, token.divisionColor);
                }
            } else {
                return i.svg
            }})
            .join();
    }

    getSwatches(token: Token, category: ExtraCategory) {
        return this.items
            .filter(i => (category === i.category) && (SpeciesRestrictions.isExtraAvailableFor(i.id, token.species, token.uniformEra)))
            .map(i => new Swatch(i.id, i.name, (token) => ExtrasCatalog.decorateSwatch(i, token), makeKey("ExtraType.", ExtraType[i.id])));
    }

    isInCategory(type: ExtraType, category: ExtraCategory) {
        return this.items.filter(i => i.id === type && i.category === category).length > 0;
    }

    static decorateSwatch(item: ExtraItem, token: Token) {
        if (item.category === ExtraCategory.Ear) {
            return `<svg viewBox="0 0 80 80" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                    <defs>
                        <clipPath id="extraClipPath-` + item.category + "-"  + item.id + `">
                            <circle cx="40" cy="40" r="40" fill="#ffffff" />
                        </clipPath>
                    </defs>
                    <g clip-path="url(#extraClipPath-` + item.category + "-" + item.id + `">
                    <g transform="translate(-165, -115)">`
                    + EarCatalog.instance.getEar(token)
                    + item.svg
                    + `</g>
                    </g>
                </svg>`;
        } else if (item.category === ExtraCategory.Headwear || item.category === ExtraCategory.Face) {
            return `<svg viewBox="0 0 240 240" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                    <defs>
                        <clipPath id="extraClipPath-` + item.category + "-"  + item.id + `">
                            <circle cx="120" cy="120" r="120" fill="#ffffff" />
                        </clipPath>
                    </defs>
                    <g clip-path="url(#extraClipPath-` + item.category + "-" + item.id + `">
                    <g transform="translate(-135, -10)">`
                    + ReferenceHead.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, token.skinColor)
                    + (token.species === Species.Ferengi ? FerengiForehead.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, token.skinColor) : "")
                    + (DivisionColors.isDivisionColorsSupported(token.uniformEra)
                        ? item.svg.replace(DefaultRed, token.uniformEra === UniformEra.DominionWar ? "#7d7d7d" : token.divisionColor)
                        : item.svg)
                    + (item.id === ExtraType.SecurityHelmet ? "" :EarCatalog.instance.getEar(token))
                    + `</g>
                    </g>
                </svg>`;
        } else {
            return `<svg viewBox="0 0 110 110" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                    <defs>
                        <clipPath id="extraClipPath-` + item.category + "-" + item.id + `">
                            <circle cx="55" cy="55" r="55" fill="#ffffff" />
                        </clipPath>
                    </defs>
                    <g clip-path="url(#extraClipPath-`  + item.category + "-" +  item.id + `">
                        <g transform="translate(-250, -70)">`
                        + Eye2StandardBrows.replace(SpeciesRestrictions.DEFAULT_HAIR_COLOR_REGEX, token.hairColor)
                        + Eye2.replace(SpeciesRestrictions.DEFAULT_EYE_COLOR_REGEX, token.eyeColor)
                        + item.svg
                    + `</g>
                    </g>
                </svg>`;
        }
    }
}

export default ExtrasCatalog;