class NoseCatalog {

    private static _instance: NoseCatalog;

    private swatches = [
        new Swatch(NoseType.StraightBasic, "Straight/Basic", (token) => NoseCatalog.decorateSwatch(Nose1, token), "NoseType.straightBasic"),
        new Swatch(NoseType.Convex, "Convex", (token) => NoseCatalog.decorateSwatch(Nose2, token), "NoseType.convex"),
        new Swatch(NoseType.SmallBulb, "Small Bulb", (token) => NoseCatalog.decorateSwatch(Nose3, token), "NoseType.smallBulb"),
        new Swatch(NoseType.Broad, "Broad", (token) => NoseCatalog.decorateSwatch(Nose4, token), "NoseType.broad"),
        new Swatch(NoseType.LongNarrow, "Long Narrow", (token) => NoseCatalog.decorateSwatch(Nose5, token), "NoseType.longNarrow"),
        new Swatch(NoseType.HighBridge, "High Bridge", (token) => NoseCatalog.decorateSwatch(Nose6, token), "NoseType.highBridge"),
        new Swatch(NoseType.Bulbous, "Bulbous", (token) => NoseCatalog.decorateSwatch(Nose7, token), "NoseType.bulbous"),
        new Swatch(NoseType.SmallFlat, "Small, Flat", (token) => NoseCatalog.decorateSwatch(Nose8, token), "NoseType.smallFlat"),
        new Swatch(NoseType.SmallUpTurned, "Small, Up-turned", (token) => NoseCatalog.decorateSwatch(Nose9, token), "NoseType.smallUpTurned"),
        new Swatch(NoseType.Hawk, "Hawk", (token) => NoseCatalog.decorateSwatch(Nose10, token), "NoseType.hawk"),
    ];

    public static get instance() {
        if (NoseCatalog._instance == null) {
            NoseCatalog._instance = new NoseCatalog();
        }
        return NoseCatalog._instance;
    }

    getNose(token: Token) {
        let result = ""
        if (token.species === Species.Tellarite) {
            result = TellariteNose1;
        } else if (token.species === Species.Ferengi) {
            return FerengiNose;
        } else if (token.noseType === NoseType.Convex) {
            result = Nose2;
        } else if (token.noseType === NoseType.SmallBulb) {
            result = Nose3;
        } else if (token.noseType === NoseType.Broad) {
            result = Nose4;
        } else if (token.noseType === NoseType.LongNarrow) {
            result = Nose5;
        } else if (token.noseType === NoseType.HighBridge) {
            result = Nose6;
        } else if (token.noseType === NoseType.Bulbous) {
            result = Nose7;
        } else if (token.noseType === NoseType.SmallFlat) {
            result = Nose8;
        } else if (token.noseType === NoseType.SmallUpTurned) {
            result = Nose9;
        } else if (token.noseType === NoseType.Hawk) {
            result = Nose10;
        } else {
            result = Nose1;
        }

        if (token.species === Species.Bajoran) {
            return BajoranNoseWrinkles + result;
        } else if (token.species === Species.Bolian) {
            let seam = "";
            switch (token.noseType) {
                case NoseType.StraightBasic:
                    seam = Nose1BolianSeam;
                    break;
                case NoseType.Convex:
                    seam = Nose2BolianSeam;
                    break;
                case NoseType.SmallBulb:
                    seam = Nose3BolianSeam;
                    break;
                case NoseType.Broad:
                    seam = Nose4BolianSeam;
                    break;
                case NoseType.LongNarrow:
                    seam = Nose5BolianSeam;
                    break;
                case NoseType.HighBridge:
                    seam = Nose6BolianSeam;
                    break;
                case NoseType.Bulbous:
                    seam = Nose7BolianSeam;
                    break;
                case NoseType.SmallFlat:
                    seam = Nose8BolianSeam;
                    break;
                case NoseType.SmallUpTurned:
                    seam = Nose9BolianSeam;
                    break;
                case NoseType.Hawk:
                    seam = Nose10BolianSeam;
                    break;
                default:

            }
            return result + seam;
        } else if (token.species === Species.Denobulan) {
            return result + DenobulanNoseAppliance.replace(SpeciesRestrictions.DEFAULT_SKIN_COLOR_REGEX, token.skinColor);
        } else if (SpeciesRestrictions.isRubberHeaded(token.species)) {
            return "";
        } else {
            return result;
        }
    }

    public getSwatches(token: Token) {
        if (SpeciesRestrictions.isRubberHeaded(token.species)) {
            return [];
        } else if (token.species === Species.Tellarite) {
            return [new Swatch(NoseType.StraightBasic, "Standard Tellarite Nose", (token) => NoseCatalog.decorateSwatch(TellariteNose1, token)) ];
        } else if (token.species === Species.Ferengi) {
            return [new Swatch(NoseType.StraightBasic, "Standard Ferengi Nose", (token) => NoseCatalog.decorateSwatch(FerengiNose, token)) ];
        } else {
            return this.swatches;
        }
    }

    private static decorateSwatch(svg: string, token: Token) {
        let result = `<svg viewBox="0 0 100 100" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <g transform="translate(-260, -100)">`
            + svg
            + (token.species === Species.Bajoran ? BajoranNoseWrinkles : "")
            + `</g>
            </svg>`;
        return result;
    }
}

export default NoseCatalog;